import { useRouter } from 'next/router'

import styled from 'styled-components'

import ImageContainer from '../Containers/ImageContainer'
import Logo from '../Logo'
import NameEmailForm from '../NameEmailForm'

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  min-height: 60vh;
  color: #000;
  padding-left: 40px;
  @media (max-width: 500px) {
    width: 60%
  }
`

const FormDiv = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
`

export default function HomePageHeader () {
  const router = useRouter()

  const handleSubmit = async ({name, email}) => {
    const { NEXT_PUBLIC_API_URL } = process.env

    const data = await fetch(`${NEXT_PUBLIC_API_URL}/secrets`, {
      method: 'POST',
      body: JSON.stringify({
        name,
        email
      })
    })
    handleResponse(await data.json())
  }

  const handleResponse = (response) => {
    if (response.success) {
      router.push(`/secret/${response.id}?adminKey=${response.adminKey}`)
    }
  }

  return (
    <ImageContainer>
      <Container>
        <Logo />
        <h2>A melhor brincadeira do natal</h2>
      </Container>
      <FormDiv>
        <NameEmailForm onSubmit={handleSubmit} />
      </FormDiv>
    </ImageContainer>
  )
}
